INSERT INTO `news` (`id`, `title`, `description`, `image`, `time`, `link`, `detail`, `view`) VALUES
(86, 'Hàng loạt dự án bất động sản ở Hà Nội đang được thế chấp tại các tổ chức tín dụng (VAMC)', 'Công ty Quản lý tài sản của các tổ chức tín dụng Việt Nam (VAMC) mới công bố hàng loạt dự án bất động sản tại Hà Nội đang được thế chấp tại VAMC. Đặc biệt, tại nhiều chung cư đang và sắp mở bán đang được thế chấp tại VAMC', 'du-an-Silk-City-voi-nhung-can-ho-the-chap.jpg', '2018-07-28', 'https://kenhbds.vn/thong-tin/hang-loat-du-an-bat-dong-san-o-ha-noi-dang-duoc-the-chap-tai-cac-to-chuc-tin-dung-vamc.html', '  	VAMC do Nhà nước sở hữu 100% vốn điều lệ và chịu sự quản lý nhà nước, thanh tra, giám sát của Ngân hàng Nhà nước. Vừa qua VAMC đã công bố hàng loạt các dự án bất động sản có thế chấp tại Hà Nội.  ', 0),
(87, 'Thị trường giao dịch ngầm đất nền tại khu Tây Tp.HCM', 'Nếu giao dịch đất nền khu Đông Tp.HCM khá trầm lắng do thiếu nguồn hàng chào bán thì nhà đất khu Tây ghi nhận khá nhiều hoạt động mua bán “ngầm” nhờ nguồn cung dồi dào và giá thành vừa phải.', 'thi-truong-giao-dich-ngam-dat-nen-phia-tay-hcm.jpg', '2018-08-04', 'https://kenhbds.vn/thong-tin/thi-truong-giao-dich-ngam-dat-nen-tai-khu-tay-tp-hcm.html', '  	<span style=\"font-size:12px;\"><span style=\"font-family:arial,helvetica,sans-serif;\">“Tuy không giao dịch rầm rộ, ngày tiếp 7-8 lượt khách như quý trước, nhưng trung bình mỗi tuần sàn vẫn thực hiện thành công được gần chục giao dịch, phần nhiều là phân khúc đất nền và đất thổ cư. Sắp bước vào giai đoạn cao điểm giao dịch nhà đất cuối năm, thời điểm nhiều CĐT ra hàng mới nên hiện tại, một lượng không nhỏ nhà đầu tư đặt hàng trước ở các sàn để tranh thủ tìm được sản phẩm ưng ý. Số lượng này hiện khá nhiều, tăng gần 30-40% so với lượng khách có nhu cầu đầu tư vào tháng 7”, vị đại diện sàn cho biết. Cũng theo vị này, riêng với loại sản phẩm từ 30-40 triệu/m2 thì tương đối khó bán vì ít có nhu cầu tìm mua.</span></span>  ', 0),
(88, 'Chủ đầu tư bị xử lý vì để dân cư dùng nước bẩn', 'UBND huyện Đan Phượng vừa có văn bản gửi UBND TP. Hà Nội đề nghị xem xét xử lý Công ty cổ phần đầu tư Hải Phát, chủ đầu tư khu đô thị (KĐT) Tân Tây Đô, và các đơn vị liên quan khi cung cấp nước sinh hoạt không đảm bảo cho cư dân sống tại đây.', 'cu-dan-tan-tay-do-phan-anh-vi-dung-nuoc-ban-qua-lau.jpg', '2018-08-04', 'https://kenhbds.vn/thong-tin/chu-dau-tu-bi-xu-ly-vi-de-dan-cu-dung-nuoc-ban.html', '  	<span style=\"font-size:12px;\">Trước tình hình đó, UBND huyện Đan Phượng đã có văn bản gửi UBND TP. Hà Nội để báo cáo cụ thể. Văn bản này cho biết KĐT Tân Tây Đô được xây dựng theo Quyết định số 1426/QĐ-UBND 28/5/2008 của UBND tỉnh Hà Tây, với nguồn nước cấp cho khu đô thị lấy từ trạm cấp nước công suất 9.000m3/ngày đêm đặt ở phía Đông thôn Hạnh Đản, sau đó sẽ bổ sung từ nguồn nước sông Đà. Tuy nhiên, trạm cấp nước trên chưa được xây dựng.</span>  ', 0),
(89, 'Nha Trang dẫn đầu cuộc đua trên thị trường bất động sản nghỉ dưỡng', 'Nha Trang đã liên tục khẳng định vị trí và thế mạnh du lịch biển. Số liệu từ đơn vị khảo sát cho thấy Nha Trang dẫn đầu trong cuộc đua bất động sản nghỉ dưỡng, vượt Đà Nẵng và Phú Quốc về cả nguồn cung và công suất phòng.', 'thi-truong-du-lich-nghi-duong-Nha-Trang.jpg', '2018-08-06', 'https://kenhbds.vn/thong-tin/nha-trang-dan-dau-cuoc-dua-tren-thi-truong-bat-dong-san-nghi-duong.html', '  	Một báo cáo về thị trường bất động sản nghỉ dưỡng của Savills cho thấy, ngay từ năm 2016 Nha Trang dẫn đầu trong cuộc đua trên thị trường bất động sản nghỉ dưỡng với tổng nguồn cung căn hộ nghỉ dưỡng lên tới 11.794 căn, gấp đôi Đà Nẵng (7.552 căn) và và bỏ xa Phú Quốc (1.201 căn).  ', 0),
(90, 'Đầu tư xây dựng 3 dự án khu du lịch tại Hà Nội, Đà Nẵng và Hà Giang', 'Dio Group và Vilandtravel sẽ hợp tác đầu tư xây dựng 3 khu du lịch, gồm: Khu du lịch sinh thái trên cung đường hạnh phúc Mã Pì Lèng (Hà Giang); khu du lịch tâm linh Đà Nẵng và công viên hoa hồng Rose Park (Hà Nội).', 'cong-vien-hoa-hong-rose-park.jpeg', '2018-08-06', 'https://kenhbds.vn/thong-tin/dau-tu-xay-dung-3-du-an-khu-du-lich-tai-ha-noi-da-nang-va-ha-giang.html', '  	<a href=\"https://kenhbds.vn/thong-tin/wp-content/uploads/2018/08/cong-vien-hoa-hong-rose-park.jpeg\"><img alt=\"\" class=\"aligncenter size-full wp-image-46680\" height=\"382\" src=\"https://kenhbds.vn/thong-tin/wp-content/uploads/2018/08/cong-vien-hoa-hong-rose-park.jpeg\" width=\"680\" /></a>  ', 0),
(91, 'Bất động sản Uông Bí - sự lựa chọn mới của giới đầu tư', 'Trước đây, nhắc đến Quảng Ninh là người ta nghĩ ngay đến mảnh đất trù phú giàu tài nguyên khoáng sản, đến kỳ quan thiên nhiên thế giới Vịnh Hạ Long, hay đến những trải nghiệm du lịch tâm linh – biển đảo hấp dẫn du khách. Nhưng giờ đây, người ta còn nhắc đ', 'bat-dong-san-uong-bi.jpg', '2018-08-06', 'https://kenhbds.vn/thong-tin/bat-dong-san-uong-bi-su-lua-chon-moi-cua-gioi-dau-tu.html', '  	Với bốn thành phố trực thuộc tỉnh là Hạ Long, Móng Cái, Cẩm Phả, và Uông Bí, một đặc khu kinh tế Vân Đồn và <a href=\"https://kenhbds.vn/thong-tin/tin-tuc-su-kien/dau-tu-xay-dung\">đầu tư</a> hàng chục nghìn tỷ đồng xây dựng hệ thống hạ tầng giao thông bao gồm sân bay, cầu cảng, cao tốc dự kiến hoạt động vào Quý III/2018…Quảng Ninh đang có những bước chuyển mình đầy “ngoạn mục”, không chỉ trong tăng trưởng kinh tế mà còn là điểm đến đầu tư hấp dẫn nhất thị trường BĐS năm 2018. Theo đó, không chỉ Hạ Long mà cơn sốt <a href=\"https://kenhbds.vn/thong-tin/\">bất động sản</a>&nbsp;đã lên đến đỉnh điểm tại Vân Đồn, TP Móng Cái và lan tỏa đến cả Uông Bí – Thành phố cửa ngõ của tỉnh Quảng Ninh.  ', 0),
(92, 'Hà Nội hiện nay không còn đủ quỹ đất để thực hiện các dự án BT mới', 'Quỹ đất thanh toán đối ứng dự kiến theo đề xuất của nhà đầu tư khoảng 9.000 ha nhưng Hà Nội mới đáp ứng khoảng 44%. ', 'tuyen-duong-bt-ha-noi.jpg', '2018-08-07', 'https://kenhbds.vn/thong-tin/ha-noi-hien-nay-khong-con-du-quy-dat-de-thuc-hien-cac-du-an-bt-moi.html', '  	Đến năm 2017, Hà Nội còn 115 dự án theo mô hình PPP.&nbsp;Đầu tháng 2/2018, Sở Kế hoạch &amp; Đầu tư đề xuất xếp 35 dự án thuộc nhóm ưu tiên triển khai. Trong đó,&nbsp;19 dự án giải quyết ùn tắc giao thông và môi trường bức xúc dân sinh đã được thành phố báo cáo Thanh tra Chính phủ đề xuất một số cơ chế đặc thù để triển khai sớm.&nbsp;9 dự án địa phương đã đủ quỹ đất thanh toán. 51 dự án còn lại không thuộc danh mục các dự án trọng điểm, cấp bách ưu tiên triển khai.&nbsp;  ', 0),
(93, 'Đại gia Casino trở lại, khởi động tại thị trường bất động sản nghỉ dưỡng Việt Nam', 'Sau nửa thập kỷ rút khỏi khu phức hợp casino Hồ Tràm, MGM bất ngờ tái xuất tại một dự án resort tại miền Trung Việt Nam.', '20170310_58c2294f0029a-130x100.jpg', '2018-08-07', 'https://kenhbds.vn/thong-tin/dai-gia-casino-tro-lai-khoi-dong-tai-thi-truong-bat-dong-san-nghi-duong-viet-nam.html', '  	Hồi tháng 11/2008, Asian Coast Development Ltd (ACDL) đã ký kết thỏa thuận với MGM sẽ thực hiện các dịch vụ tiền khai trương và điều hành quản lý khu nghỉ dưỡng đầu tiên thuộc tổ hợp du lịch Hồ Tràm Strip do ACDL phát triển.  ', 0),
(94, 'Xây nhà chọc trời ở Việt Nam đang dần trở thành \"xu thế\"', 'Các kỷ lục về nhà chọc trời liên tục bị phá vỡ, nhưng cuộc đua \"cao nhất\" có vẻ chưa dừng lại, khi liên tiếp những dự án lớn ra mắt thị trường.  ', 'toa-nha-choc-troi-o-viet-nam.jpg', '2018-08-07', 'https://kenhbds.vn/thong-tin/xay-nha-choc-troi-o-viet-nam-dang-dan-tro-thanh-xu-the.html', '  	Tổ hợp Keangnam Landmark Tower (quận Nam Từ Liêm, Hà Nội) với chiều cao 336 m, khi hoàn thành vào năm 2011, lập kỷ lục tòa nhà cao nhất Việt Nam và ở vị trí này cho đến tháng 7/2018.  ', 0),
(95, 'Hà Nội xin ý kiến quy hoạch Phú Xuyên thành khu đô thị vệ tinh', '(DĐDN) - Đồ án quy hoạch chung đô thị vệ tinh Phú Xuyên, Hà Nội đang xin ý kiến của Bộ Xây dựng trước khi trình UBND Thành phố phê duyệt.', 'khu-do-thi-ve-tinh-phu-xuyen.jpg', '2018-08-08', 'https://kenhbds.vn/thong-tin/ha-noi-xin-y-kien-quy-hoach-phu-xuyen-thanh-khu-do-thi-ve-tinh.html', '  	Trước đó, Đồ án <a href=\"https://kenhbds.vn/thong-tin/tin-tuc-su-kien/chinh-sach-quy-hoach\">Quy hoạch</a> chung xây dựng đô thị vệ tinh Phú Xuyên tỷ lệ 1/5000 đã được UBND Thành phố phê duyệt Nhiệm vụ tại Quyết định số 3240/QĐ-UBND ngày 18/7/2012 và phê duyệt danh mục các đồ án quy hoạch, quy chế quản lý quy hoạch kiến trúc, thiết kế đô thị năm 2015 trên địa bàn Thành phố vào ngày 24/9/2014. Theo đó, đồ án nằm trong Quy hoạch chung xây dựng Thủ đô Hà Nội đến năm 2030 và tầm nhìn đến năm 2050, phát triển <a href=\"https://kenhbds.vn/can-ban/ha-noi-t24\">Hà Nội </a>theo mô hình chùm đô thị gồm khu vực đô thị trung tâm và 5 đô thị vệ tinh gồm: đô thị Hòa Lạc, Sơn Tây, Xuân Mai, Phú Xuyên và Sóc Sơn. Các khu vệ tinh được kết nối bằng hệ thống giao thông đường vành đai kết hợp các trục hướng tâm. Mỗi đô thị có chức năng hỗn hợp và đặc thù riêng, hoạt động tương đối độc lập để hỗ trợ và chia sẻ với đô thị trung tâm về nhà ở, đào tạo, công nghiệp, dịch vụ... . Dân số ước tính 13 - 15 vạn người.  ', 0),
(96, 'Bất động sản TP.HCM: Nhà phố, biệt thự đang \"cháy hàng\"', 'Biệt thự và nhà phố là sản phẩm duy trì được lượng giao dịch lớn từ đầu năm đến nay, do tâm lý thích ở nhà đất của nhiều người dân.', '20170809_598a76024536f.jpg', '2017-08-10', 'https://kenhbds.vn/thong-tin/bat-dong-san-tp-hcm-nha-pho-biet-thu-dang-chay-hang.html', '  	Khu vực Đông và Nam TP.HCM là nơi có tốc độ bán hàng cao nhất trong 2 quý đầu năm 2017. Mặc dù, toàn cảnh thị trường cho thấy sự chững lại, nhưng trên thực tế không phải do nhu cầu sụt giảm, mà chính ở nguồn cung chất lượng không đáp ứng được nhu cầu thị trường.  ', 0),
(97, 'Danh sách những chung cư đang \"sống chung\" với giặc lửa', 'Trong số này có những dự án quy mô lớn, dân số đông như: Khối đế và tháp B (văn phòng) thuộc tòa nhà hỗn hợp HH1, ngõ 102 Trường Chinh (quận Thanh Xuân), Chung cư N09B2 Khu đô thị mới Dịch Vọng (quận Cầu Giấy), Khu đô thị Dream Town, Tây Mỗ, Nam Từ Liêm (', '20170809_598a87512fb45.jpg', '2017-08-10', 'https://kenhbds.vn/thong-tin/danh-sach-nhung-chung-cu-dang-song-chung-voi-giac-lua.html', '  					<strong>STT</strong>  				', 0),
(98, 'Đà Nẵng: Cuối 2017, nguồn cung khách sạn 3-5 sao sẽ đạt hơn 19.000 phòng', 'Công bố mới nhất của Công ty CBRE Việt Nam (CBRE) tại buổi họp báo về thị trường bất động sản Đà Nẵng Quý II-2017 diễn ra ngày 9/8 cho thấy, 6 tháng đầu năm 2017, Đà Nẵng đón nhận thêm 1 khách sạn 5 sao và 3 khách sạn 4 sao với số lượng phòng được cung cấ', '20170810_598bdbcfd227c.jpg', '2017-08-10', 'https://kenhbds.vn/thong-tin/da-nang-cuoi-2017-nguon-cung-khach-san-3-5-sao-se-dat-hon-19-000-phong.html', '  	“Nhìn chung, trong Quý II-2017, các khách sạn ghi nhận kết quả kinh doanh tốt hơn so với cùng kỳ năm trước”, Bà Dung cho biết.  ', 0),
(99, 'M&A: Chiến lược mới của doanh nghiệp địa ốc', 'Trong chiến lược phát triển dài hơi của các doanh nghiệp bất động sản, việc nắm giữ quỹ đất được xem là chìa khóa để thành công. Do vậy, nhiều “ông lớn” đã không ngại đổ hàng ngàn tỷ đồng cho M&A dự án địa ốc.', '20170810_598bbec7e4e11.jpg', '2017-08-10', 'https://kenhbds.vn/thong-tin/ma-chien-luoc-moi-cua-doanh-nghiep-dia-oc.html', '  	Trước năm 2013, Tập đoàn Novaland chỉ có vỏn vẹn vài dự án, song đến thời điểm hiện nay, Novaland đã nắm trong tay hơn 40 dự án bất động sản có quy mô lớn. Một trong những thương vụ M&amp;A dự án của Novaland được chú ý là vụ chi hơn 100 triệu USD mua lại hơn 80% cổ phần của Công ty cổ phần Phát triển quốc tế Thế kỷ 21 (C21), qua đó toàn quyền quản lý, điều hành và phát triển Dự án Nam Rạch Chiếc thuộc quận 2, TP.HCM.&nbsp; Đây là dự án có quy mô tới 30 ha,&nbsp; bị đắp chiếu trong nhiều năm qua. Sau khi mua dự án này, Novaland biến khu đất hoang hóa này thành một khu đô thị hiện đại mang tên Lakeview City.  ', 0),
(100, 'Ông chủ Tân Hoàng Minh nắm trong tay “đất vàng” 94 Lò Đúc xây cao ốc?', 'C&#244;ng ty Thi&#234;n B&#236;nh l&#224; đơn vị ph&#225;t triển dự &#225;n cao ốc 94 L&#242; Đ&#250;c...', '20170416_58f2ca857a6bc-120x120.jpg', '2017-08-10', 'https://kenhbds.vn/thong-tin/ong-chu-tan-hoang-minh-nam-trong-tay-dat-vang-94-lo-duc-xay-cao-oc.html', '  	Được biết, đơn vị triển khai và thực hiện dự án là Công ty TNHH Đầu tư Kinh doanh và Phát triển đô thị Thiên Bình. Công ty này được thành lập từ tháng 6/2013 với vốn điều lệ 466,6 tỷ đồng.  ', 0),
(101, 'Đổ tiền mua biệt thự cao cấp, ký xong hợp đồng lỗ ngay 1 tỷ đồng', 'Vừa mua xong căn biệt thự, chỉ khoảng thời gian ngắn sau chủ nhà đã lỗ gần 1 tỷ nếu rao bán. Không ít người rơi vào ngậm đắng nuốt cay khi xuống tiền mua biệt thự ven đô. Xa trung tâm, hạ tầng chưa hoàn thiện, thiếu nhiều tiện ích... khiến người mua vỡ mộ', '20170930_59cfbcc4367ff.jpg', '2017-09-30', 'https://kenhbds.vn/thong-tin/do-tien-mua-biet-thu-cao-cap-ky-xong-hop-dong-lo-ngay-1-ty-dong.html', '  	Chị Nguyễn Thu Huyền, chủ một căn biệt thự tại khu vực phía Tây Hà Hội, rất bức xúc khi nói về căn nhà của mình. Chị mua nó cách đây chưa đầy 1 năm do bùi tai trước những lời quảng cáo như \"rót mật\" của chủ đầu tư. Song, đến khi xuống tiền, chị mới ngã ngửa: thương hiệu nhà chị mua là do chủ đầu tư hợp tác với một công ty BĐS nữa. Chưa kể, nhiều tiện ích lại thuộc dự án khác.  ', 0),
(102, 'Thị trường bất động sản: Chốt lời thời điểm này là việc khó', 'Theo báo cáo vừa được CBRE cập nhật, thị trường chung cư Hà Nội tiếp tục chứng kiến nguồn cung đang gia tăng nhanh chóng, gây áp lực giảm giá cho toàn thị trường.', '20170930_59cefb435868e.jpg', '2017-09-30', 'https://kenhbds.vn/thong-tin/thi-truong-bat-dong-san-chot-loi-thoi-diem-nay-la-viec-kho.html', '  	Về mức giá bán, Bà Nguyễn Hoài An cho biết, có giảm nhẹ so với quý trước. Trong đó, mức tăng trưởng tương đối nhanh về nguồn cung sơ cấp phần nào tạo áp lực cho thị trường, giá trung bình có xu hướng giảm nhất là tại các đã hoàn thiện với mức giảm 3% theo quý, tương đương 12% theo năm.  ', 0),
(103, 'Tại Phú Quốc: trên 1.000 phôi sổ đỏ bị mất', 'Chiều 19/7, đại tá Phạm Trung Thành, người phát ngôn của Công an tỉnh Kiên Giang, xác nhận cơ quan điều tra đã gửi thông báo đến tất cả ngân hàng trong tỉnh để truy tìm phôi giấy chứng nhận quyền sử dụng đất (sổ đỏ) bị mất ở Phú Quốc.', 'hon-1000-phoi-so-do-o-phu-quoc-bong-nhien-bien-mat.jpg', '2018-07-20', 'https://kenhbds.vn/thong-tin/tai-phu-quoc-tren-1-000-phoi-so-do-bi-mat.html', '  	Theo thông báo do đại tá Trương Đông Hải ký, vào tháng 3/2017, Phòng Cảnh sát kinh tế Công an tỉnh Kiên Giang khởi tố vụ án Tham ô tài sản và Thiếu trách nhiệm gây hậu quả nghiêm trọng xảy ra tại Chi nhánh Văn phòng Đăng ký đất đai huyện Phú Quốc. Cơ qua điều tra xác định một số tài liệu liên quan đến vụ án bị thất lạc từ năm 2014 đến nay.  ', 0),
(104, 'Quy hoạch khu đất đấu giá gần 66.000m2 tại Thường Tín', 'Mục tiêu của Hà Nội khi phê duyệt quy hoạch này nhằm xây dựng khu nhà ở mới hiện đại, đồng bộ về cơ sở hạ tầng xã hội, hạ tầng kỹ thuật, khớp nối với các đồ án quy hoạch, dự án đầu tư và khu dân cư lân cận đảm bảo phát triển ổn định, bền vững.  ', '20170626_59509065c55d0.jpg', '2018-07-23', 'https://kenhbds.vn/thong-tin/quy-hoach-khu-dat-dau-gia-gan-66-000m2-tai-thuong-tin.html', '  	Khu đất được quy hoạch có phía Tây Bắc giáp đường quy hoạch; phía Tây Nam giáp cụm công trình hỗn hợp theo quy hoạch; phía Đông Bắc giáp đường quy hoạch; phía Nam giáp khu đất làng xóm đô thị hóa xã Vạn Điểm.  ', 0),
(105, 'Chủ đầu tư bất động sản bị phạt số tiền \"khủng\" 1,64 tỷ đồng', 'Công ty TNHH Đầu tư Bất động sản Tân Bình vừa bị UBND TPHCM ra quyết định xử phạt vi phạm hành chính với số tiền “khủng” lên tới 1,64 tỷ đồng.', 'tan-binh-apartment-1-15325764065071631623576.jpg', '2018-07-26', 'https://kenhbds.vn/thong-tin/chu-dau-tu-bat-dong-san-bi-phat-so-tien-khung-164-ty-dong.html', '  	<a href=\"https://kenhbds.vn/thong-tin/wp-content/uploads/2018/07/tan-binh-apartment-1-15325764065071631623576.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-46610\" height=\"659\" src=\"https://kenhbds.vn/thong-tin/wp-content/uploads/2018/07/tan-binh-apartment-1-15325764065071631623576.jpg\" width=\"640\" /></a>  ', 0),
(106, 'Vingroup ra mắt dự án căn hộ Vinhomes Bắc Ninh', 'Tập đo&#224;n Vingroup vừa ch&#237;nh thức ra mắt Tổ hợp căn hộ cao cấp đầu ti&#234;n tại Bắc Ninh - Vinhomes Bắc Ninh...', '20170320_58cfa3f3d9fc9-120x120.jpg', '2017-07-08', 'https://kenhbds.vn/thong-tin/vingroup-ra-mat-du-an-can-ho-vinhomes-bac-ninh.html', '  	Là tổ hợp căn hộ cao cấp trên quy mô gần 2,5ha, Vinhomes Bắc Ninh được quy hoạch đa chức năng gồm 2 tòa căn hộ cao 27 và 31 tầng; 1 trung tâm thương mại tại 4 tầng khối đế và hệ thống cảnh quan, tiện ích đồng bộ, hiện đại. Trong đó, điểm nhấn là 2 tòa căn hộ cao cấp được thiết kế thông minh, nội thất liền tường.  ', 0),
(107, 'Thanh tra Hà Nội: Xử nghiêm vi phạm đất quốc phòng tại Đồng Tâm', 'C&#244;ng bố dự thảo kết luận thanh tra to&#224;n diện đất đai khu s&#226;n bay Miếu M&#244;n, x&#227; Đồng T&#226;m, huyện Mỹ Đức...', '20170316_58c9f22a710cb-130x100.jpg', '2017-07-08', 'https://kenhbds.vn/thong-tin/thanh-tra-ha-noi-xu-nghiem-vi-pham-dat-quoc-phong-tai-dong-tam.html', '  	<strong>Chính quyền xã đã làm trái thẩm quyền</strong>  ', 0),
(108, 'Tp.HCM: Nhà phố - biệt thự quận 9 có “hạ sốt” theo đất nền?', 'Lợi thế h&#224;ng đầu của quận 9, Tp.HCM kh&#244;ng chỉ nằm ở vị tr&#237; th&#237;ch hợp, cơ sở hạ tầng được đầu tư đồng bộ, m&#224; c&#242;n ở khu đ&#244; thị tr&#237; thức v&#224; khu c&#244;ng nghệ cao...', '20170313_58c5f2e7c9794-130x100.jpg', '2017-07-08', 'https://kenhbds.vn/thong-tin/tp-hcm-nha-pho-biet-thu-quan-9-co-ha-sot-theo-dat-nen.html', '  	Báo cáo mới nhất của Jones Lang LaSalle (JLL) dường như đi ngược với động thái của thị trường khi đưa ra con số lạc quan về nguồn cung biệt thự, nhà phố cuối năm nay (2017). Tại phân khúc biệt thự, nhà phố, nguồn cung mới trong quý 2/2017 đạt 1.381 căn, tăng 3% theo quý và 40% theo năm, chủ yếu đến từ quận 2, quận 9 và Nhà Bè.  ', 0),
(109, 'TP.HCM: Sắp có căn hộ giá hơn 200 triệu đồng một... mét vuông', 'Căn hộ giá 8.000-10.000 USD một mét vuông, cao nhất TP.HCM hiện nay, cả dự án không tới 100 sản phẩm, có thể được công bố trong năm 2017.', '20170707_595f140d9a89f.jpg', '2017-07-08', 'https://kenhbds.vn/thong-tin/tp-hcm-sap-co-can-ho-gia-hon-200-trieu-dong-mot-met-vuong.html', '  	Vị trí của dự án nằm trên một tuyến đường thuộc quận 1, TP HCM. Nếu việc công bố dự án diễn ra đúng như dự kiến, đây sẽ là lần đầu tiên thị trường Việt Nam xuất hiện dòng sản phẩm đặc biệt này, bà Dung cho biết.  ', 0),
(110, 'Sức hút sinh lời bất động sản nghỉ dưỡng Phú Quốc', 'Sự tăng mạnh về nhu cầu du lịch c&#249;ng nhiều ch&#237;nh s&#225;ch ưu đ&#227;i để Ph&#250; Quốc trở th&#224;nh đặc khu kinh tế h&#224;nh ch&#237;nh v&#224; l&#224; trung t&#226;m nghỉ dưỡng của khu vực trong tương lai...', '20170403_58e1b41769946-130x100.jpg', '2017-07-11', 'https://kenhbds.vn/thong-tin/suc-hut-sinh-loi-bat-dong-san-nghi-duong-phu-quoc.html', '  	Được mệnh danh là Đảo Ngọc với những bãi cát tự nhiên đẹp hàng đầu thế giới, có nhiều đồi núi, 2/3 là rừng nguyên sinh với hệ sinh thái phong phú, khí hậu ôn hòa…, nằm cách thủ đô 10 nước ASEAN chỉ khoảng 2 giờ bay, Phú Quốc hiện là điểm đến lý tưởng cho đông đảo du khách trong và ngoài nước.  ', 0),
(111, 'Lập hội đồng thẩm định tuyến metro 41 nghìn tỷ tại Tp.HCM', 'Dự &#225;n x&#226;y dựng tuyến metro số 5 giai đoạn 1 c&#243; chiều d&#224;i 8,9 km, trong đ&#243; 7,458 km đi ngầm, 1,433 km tr&#234;n cao...', '20170316_58c9e401a68b7-120x120.jpg', '2017-07-11', 'https://kenhbds.vn/thong-tin/lap-hoi-dong-tham-dinh-tuyen-metro-41-nghin-ty-tai-tp-hcm.html', '  	Ủy viên Hội đồng thẩm định là lãnh đạo các cơ quan: Văn phòng Chính phủ, Bộ Giao thông vận tải, Bộ Tài chính, Bộ Xây dựng, Bộ Tài nguyên và Môi trường, Bộ Quốc phòng, Bộ Công an, Bộ Khoa học và Công nghệ, Bộ Ngoại giao, Bộ Tư Pháp, Bộ Văn hóa - Thể thao và Du lịch, Bộ Thông tin và Truyền thông, Ngân hàng Nhà nước và UBND Tp.HCM. Cơ quan thường trực Hội đồng là Bộ Kế hoạch và Đầu tư.  ', 0),
(112, 'Hà Nội yêu cầu chủ đầu tư khu nhà ở Thượng Thanh không được chuyển nhượng dự án', 'UBND TP Hà Nội vừa ban hành Công văn số 3331/UBND-SXD cho ý kiến về việc chấp thuận chủ trương đầu tư dự án xây dựng khu nhà ở Thượng Thanh tại tổ 18, phường Thượng Thanh, quận Long Biên.', '20170713_5966d0877ac0a.jpg', '2017-07-13', 'https://kenhbds.vn/thong-tin/ha-noi-yeu-cau-chu-dau-tu-khu-nha-o-thuong-thanh-khong-duoc-chuyen-nhuong-du-an.html', '  	UBND TP. Hà Nội cũng yêu cầu chủ đầu tư không được chuyển nhượng dự án cho nhà đầu tư khác dưới bất kỳ hình thức nào khi chưa được cơ quan nhà nước có thẩm quyền cho phép theo quy định của pháp luật. Không được chuyển đơn vị khác thực hiện toàn bộ hoặc một phần dự án khi chưa được UBND thành phố chấp thuận...  ', 0),
(113, 'FLC mang xu hướng nghỉ dưỡng mới tới Hạ Long', 'FLC Hạ Long l&#224; loại h&#236;nh sản phẩm để ở c&#243; sổ đỏ ri&#234;ng, sở hữu l&#226;u d&#224;i...', '20170705_595c425355aa5-130x100.jpg', '2017-07-13', 'https://kenhbds.vn/thong-tin/flc-mang-xu-huong-nghi-duong-moi-toi-ha-long.html', '  	Không phải tự nhiên, một hòn đảo nằm trên tàn dư của một miệng núi lửa tại Hy Lạp như Santorini lại được ví như “thiên đường chốn hạ giới”, một điểm đến nghỉ dưỡng ưa thích của giới nhà giàu trong thời gian gần đây.  ', 0),
(114, 'Sắp ra mắt dự án bất động sản mang kiến trúc Venice - Ý', 'Với mong muốn x&#226;y dựng n&#234;n một bản thu nhỏ của Venice, MIK Group cho biết đang chuẩn bị cho ra mắt một “th&#224;nh phố Venice” ngay tại khu Đ&#244;ng Tp.HCM...', '20170313_58c6099ed8278-130x100.jpg', '2017-07-13', 'https://kenhbds.vn/thong-tin/sap-ra-mat-du-an-bat-dong-san-mang-kien-truc-venice-y.html', '  	Những ngôi nhà ở Venice thường có màu sắc tươi tắn với đặc trưng nhiều cửa sổ và thường có hoa tươi rủ chùm rực rỡ. Kiến trúc đa sắc nhất tại đây chính là Burano - một quần thể 4 hòn đảo ấn tượng nhất được nối với nhau bằng những cây cầu thuộc thành phố Venice. Quần đảo này được mọi người biết tới bởi những ngôi nhà đầy màu sắc được phối hoàn toàn có chủ ý. Chính điều này đã tạo nên một Burano quyến rũ và cuốn hút đến khó tả.  ', 0),
(115, 'Đất Đan Phượng, Hoài Đức rục rịch tăng giá nhờ quy hoạch', 'Thi trường nh&#224; đất khu vực Đan Phương - Ho&#224;i Đức đang được hưởng lợi sau khi Ch&#237;nh phủ đồng &#253; chủ trương cho ph&#233;p H&#224; Nội triển khai dự &#225;n đường T&#226;y Thăng Long...', '20170310_58c27ec358dab-130x100.jpg', '2017-07-13', 'https://kenhbds.vn/thong-tin/dat-dan-phuong-hoai-duc-ruc-rich-tang-gia-nho-quy-hoach.html', '  	Theo quy hoạch, trục Tây Thăng Long có tổng chiều dài khoảng 23km, là tuyến kết nối khu vực Tây Hồ Tây và phía Bắc Cầu Thăng Long với khu đô thị Sơn Tây sẽ chạy qua địa bàn huyện Đan Phượng. Khi văn bản quy hoạch chính thức được đưa ra đã khiến thị trường đất nền tại khu vực này, đặc biệt là quanh khu vực Đan Phương có dấu hiệu tăng giá.  ', 0),
(116, 'Những tên tuổi phía sau dự án Mizuki Park', 'Công ty cổ phần Đầu tư Nam Long (NLG) vừa chính thức công bố dự án khu đô thị Mizuki Park quy mô 26 ha tại Nam Sài Gòn. Thông tin này lập tức thu hút sự quan tâm của thị trường bởi quy mô dự án rất lớn và những đối tác nổi tiếng tham gia cùng Nam Long.', '20170622_594b6ae8b6c58.jpg', '2017-06-22', 'https://kenhbds.vn/thong-tin/nhung-ten-tuoi-phia-sau-du-an-mizuki-park.html', '  	Chính vì vậy, chúng tôi đã tiến hành rất nhiều cuộc thi xét chọn đối tác để đảm bảo công trình được hoàn thiện tốt nhất phục vụ nhu cầu an cư của người dân, cũng như tạo ra một dấu ấn trong lộ trình phát triển của Nam Long”.  ', 0),
(117, 'Đà Nẵng: Khách du lịch tăng đột biến, khách sạn, condotel hốt bạc', 'Đà Nẵng đã khởi đầu mùa du lịch 2017 không thể ấn tượng hơn với gần 9.500 tỷ đồng đóng góp cho ngân sách. Lĩnh vực kinh doanh khách sạn, condotel nhanh chóng được mùa khi công suất phòng liên tục vượt ngưỡng.', '20170622_594b4e0652afa.jpg', '2017-06-22', 'https://kenhbds.vn/thong-tin/da-nang-khach-du-lich-tang-dot-bien-khach-san-condotel-hot-bac.html', '  	Chỉ riêng tháng 5/2017, thời điểm diễn ra lễ hội pháo hoa quốc tế DIFF 2017, Đà Nẵng đã đón hơn 600.000 lượt khách với doanh thu gần 1.800 tỷ đồng.  ', 0),
(118, 'Vũng Tàu, thị trường Condotel mới nổi', 'Theo đánh giá của Savills Việt Nam, năm 2017, BĐS nghỉ dưỡng vẫn là phân khúc nhận được nhiều sự kỳ vọng của nhà đầu tư, nhất là trong bối cảnh lượng du khách ngày càng tăng, cùng sự quan tâm đầu tư mạnh mẽ của các cấp chính quyền, từ Trung ương tới địa p', '20170622_594b6aea13b4e.jpg', '2017-06-22', 'https://kenhbds.vn/thong-tin/vung-tau-thi-truong-condotel-moi-noi.html', '  	Bên cạnh những khu vực nói trên, cho đến thời điểm hiện nay, BĐS Việt Nam đang ghi nhận sự dịch chuyển về mặt địa lý trong phân khúc BĐS nghỉ dưỡng. Trong quý I/2017, thị trường đã chứng kiến sự chuyển động khá rầm rộ tại Phan Thiết, Quy Nhơn, Phú Yên, Quảng Nam, Sapa, Quảng Ninh, Vĩnh Phúc… và sắp tới sẽ là Bà Rịa – Vũng Tàu.  ', 0),
(119, 'TP HCM: Tin vui cho dân vùng quy hoạch \"treo\"', 'Người dân trong vùng quy hoạch \"treo\" ở TP HCM sẽ \"dễ thở\" hơn trong việc xây dựng kể từ ngày 30-6 tới đây.', '20170626_59509065c55d0.jpg', '2017-06-26', 'https://kenhbds.vn/thong-tin/tp-hcm-tin-vui-cho-dan-vung-quy-hoach-treo.html', '  		&nbsp;  	', 0),
(120, 'Vingroup sẽ có thể rót 100.000 tỷ đồng vào dự án đường sắt đô thị nào?', 'Dự án mà Vingroup định triển khai là những đoạn đường sắt đô thị đầu tiên tại Việt Nam được xây dựng hoàn toàn bằng nguồn vốn tư nhân.', '20170626_595081e24559c.png', '2017-06-26', 'https://kenhbds.vn/thong-tin/vingroup-se-co-the-rot-100-000-ty-dong-vao-du-an-duong-sat-do-thi-nao.html', '  	Ngày 4/2/2017 trong buổi làm việc với lãnh đạo các bộ, ngành, TP. Hà Nội nhằm đẩy nhanh tiến độ xây dựng hai tuyến đường sắt đô thị Cát Linh - Hà Đông và Nhổn - Ga Hà Nội, Phó thủ tướng Trịnh Đình Dũng đã có chỉ đạo cần tăng cường huy động nguồn vốn nội địa thực hiện các dự án đường sắt đô thị.  ', 0),
(121, 'Bất động sản nghỉ dưỡng: “Để thành công phải tạo ra xu hướng mới”', 'Trong 6 th&#225;ng đầu năm đ&#227; c&#243; tr&#234;n 12.000 căn hộ đến từ 78 dự &#225;n nghỉ dưỡng được bung ra thị trường...', '20170711_59642ce926cc1-130x100.gif', '2017-07-05', 'https://kenhbds.vn/thong-tin/bat-dong-san-nghi-duong-de-thanh-cong-phai-tao-ra-xu-huong-moi.html', '  	Theo số liệu từ Tổng cục Thống kê, 6 tháng đầu năm 2017, khách quốc tế đến nước ta đạt trên 6,2 triệu lượt người, tăng 30,2% so với cùng kỳ năm trước. Sự tăng trưởng này được tiếp nối con số kỷ lục của năm 2016, khi Việt Nam chào đón hơn 10 triệu khách du lịch quốc tế và dự kiến lên đến 11,5 triệu vào cuối năm 2017.  ', 0),
(122, 'Bất động sản nghỉ dưỡng: Cơ hội lớn, rủi ro lớn', 'Bất động sản nghỉ dưỡng gần đây được nhắc đến như là một “ngôi sao sáng” của thị trường bất động sản, thu hút nhiều nhà đầu tư. Tuy nhiên, đầu tư vào phân khúc này, ngoài cơ hội còn có những rủi ro mà nhà đầu tư cần chú ý.', '20170701_59572aff42aec.jpg', '2017-07-05', 'https://kenhbds.vn/thong-tin/bat-dong-san-nghi-duong-co-hoi-lon-rui-ro-lon.html', '  	Việt Nam chưa làm được như những nước có bất động sản du lịch phát triển khác, dù lợi thế của Việt Nam có nhiều. Ngoài các di sản độc đáo, thì chúng ta chưa tạo ra những yếu tố khác có thể hút khách du lịch nước ngoài.  ', 0),
(123, 'Thị trường bất động sản: Đủ chiêu \"câu khách\"', '“Em Lộc nè, em có căn hộ chung cư M ở quận 7 giá chỉ 900 triệu, lợi nhuận cam kết 20%, anh coi mua đầu tư lời lắm, nhà thiết kế 2, 3 phòng ngủ vị trí đẹp… Nếu hấp dẫn, anh gọi lại em nhá”.', '20170706_595dfd9f1583c.jpg', '2017-07-06', 'https://kenhbds.vn/thong-tin/thi-truong-bat-dong-san-du-chieu-cau-khach.html', '  	Đánh giá thị trường bất động sản 6 tháng đầu năm 2017, các chủ đầu tư dự án căn hộ đều cho biết, thị trường có chững lại. Đây cũng là thời điểm các chủ đầu tư đau đầu vì nghĩ cách để quảng bá dự án, bởi họ thừa hiểu một điều, khi đẩy mạnh quảng cáo dự án, khách hàng sẽ biết và quan tâm tới dự án nhiều hơn, thì sẽ bán rất nhanh. Còn nếu không đầu tư vào quảng cáo, không đưa thông tin đến được với khách hàng, thì dự án sẽ rất “chật vật” để bán.  ', 0),
(124, 'Tổ hợp giải trí 11.000 tỷ tại Đà Nẵng sắp vận hành', 'Tổ hợp Cocobay chuẩn bị đi v&#224;o vận h&#224;nh đang tạo n&#234;n sức lan toản kh&#244;ng chỉ nội tại dự &#225;n, m&#224; cả với c&#225;c dự &#225;n ở xung quanh tại Đ&#224; Nẵng...', '20170313_58c5f2e7c9794-130x100.jpg', '2017-07-06', 'https://kenhbds.vn/thong-tin/to-hop-giai-tri-11-000-ty-tai-da-nang-sap-van-hanh.html', '  	Các toà condotel thuộc giai đoạn 2 như Coco Skyline Resort đã được thi công tới tầng 21, Coco Ocean Resort được thi công tới tầng 15…  ', 0),
(125, 'Phú Long cất nóc công trình Dragon Hill Residence and Suites 2 - Nam Sài Gòn', 'Công ty cổ phần Địa ốc Phú Long cho biết, cuối tháng 6 vừa qua, dự án Dragon Hill Residence and Suites 2 đã hoàn tất thi công cất nóc 2 toà tháp căn hộ, đánh dấu mốc quan trọng trong việc hoàn thành xây dựng phần thô và bước vài giai đoạn hoàn thiện để kị', '20170706_595dd17738014.jpg', '2017-07-06', 'https://kenhbds.vn/thong-tin/phu-long-cat-noc-cong-trinh-dragon-hill-residence-and-suites-2-nam-sai-gon.html', '  	Ở Dragon Hill Residence and Suites 2 có đầy đủ dịch vụ tiện ích phục vụ cuộc sống chất lượng cao, tiện nghi, hiện đại của cư dân như sảnh đón khách sang trọng, hồ bơi resort trên không, sân tennis, khu BBQ, phòng tập gym, vườn treo, siêu thị, nhà hàng, café, kinder garden... Ngoài ra, Công ty Phú Long đã chỉ định CBRE là đơn vị quản lý, vận hành Dragon Hill Residence and Suites 2 khi toà nhà đi vào hoạt động theo những chuẩn mực quốc tế.  ', 0),
(127, '“Cú hích” mới cho bất động sản Bình Dương', 'Nằm trong vùng kinh tế trọng điểm phía Nam, Bình Dương là địa phương nằm trong nhóm các tỉnh, thành có tốc độ phát triển kinh tế và cơ sở hạ tầng tốt nhất. Mới đây, chính quyền tỉnh đã công bố định hướng chiến lược phát triển mới hứa hẹn nhiều bứt phá, đặ', '20170601_592f7575bbf89.jpg', '2017-06-02', 'https://kenhbds.vn/thong-tin/cu-hich-moi-cho-bat-dong-san-binh-duong.html', '  	Năm 2016, Bình Dương đã thu hút được 2,4 tỉ đô la Mỹ vốn đầu tư nước ngoài (FDI) và 5 tháng đầu năm nay đã thu hút thêm gần 1,5 tỉ đô la Mỹ, vượt 106% so với kế hoạch đề ra. Đến nay, toàn tỉnh Bình Dương đã có 28 khu công nghiệp với diện tích hơn 10.000 ha và 10 cụm công nghiệp với diện tích 707 ha.  ', 0),
(128, 'Sau hơn 20 năm quy hoạch, Khu đô thị mới Thủ Thiêm hiện nay ra sao?', 'Người ta vẫn ví Thủ Thiêm là “Phố Đông” của trung tâm Sài Gòn, đang từng ngày thay da đổi thịt. Nếu ai đi qua đây sẽ thấy đại công trình đang tích cực triển khai ngày đêm. Nhiều con đường, khu phố đã và đang thành hình rõ nét từng ngày, tạo nên một sức số', '20170609_593a253f39806.jpg', '2017-06-11', 'https://kenhbds.vn/thong-tin/sau-hon-20-nam-quy-hoach-khu-do-thi-moi-thu-thiem-hien-nay-ra-sao.html', '  	Sau hai công trình trọng điểm trên, TP tiếp tục kêu gọi đầu tư hàng loạt công trình giao thông khác để kết nối giao thông cũng như tạo nền tảng bứt phá cho cả một khu vực. Đầu tiên là 4 tuyến đường chính ở Khu đô thị mới Thủ Thiêm, tiếp đến là cầu Thủ Thiêm 2. Sau khi hoàn thành 4 tuyến đường chính cùng với cầu Thủ Thiêm 1, đại lộ Đông Tây (đoạn qua Thủ Thiêm gồm đường hầm vượt sông Sài Gòn, đường Mai Chí Thọ) và các cầu Thủ Thiêm 2, 3, 4 sẽ hình thành hệ thống giao thông hoàn chỉnh cho Khu đô thị mới Thủ Thiêm.  ', 0),
(129, '“Lời nguyền” ở khu đất cửa ngõ Đông Sài Gòn', 'Ngay trong lòng khu Đông trong khi nhiều dự án mới lẫn cũ sôi động giao dịch thì vẫn có khá nhiều dự án gồm cả mới lẫn cũ khó tìm lối thoát.', '20170609_593a4fc44b2dc.jpg', '2017-06-11', 'https://kenhbds.vn/thong-tin/loi-nguyen-o-khu-dat-cua-ngo-dong-sai-gon.html', '  	Nếu ai hay di chuyển trên con đường Đồng Văn Cống, quận 2 (hướng xuống phà Cát Lái) sẽ dễ nhìn thấy cảnh tượng e ngại. Đó là dãy biệt thự cách mặt tiền đường vài bước chân nằm bất động bấy lâu nay.  ', 0),
(130, 'Chính thức ra mắt FLC Grand Hotel Hạ Long vào ngày 25/6', 'FLC Grand Hotel Hạ Long, dự &#225;n căn hộ kh&#225;ch sạn 5 sao của Tập đo&#224;n FLC sẽ ch&#237;nh thức ra mắt tại H&#224; Nội v&#224;o ng&#224;y 25/6 tới đ&#226;y...', '20170428_5902edd84a936-130x100.jpg', '2017-06-21', 'https://kenhbds.vn/thong-tin/chinh-thuc-ra-mat-flc-grand-hotel-ha-long-vao-ngay-256.html', '  	Tọa lạc trên đỉnh cao nhất của đồi Văn Nghệ, những khung cửa của FLC Grand Hotel Hạ Long mở ra một bức tranh thủy mặc giữa thiên nhiên hùng vĩ.  ', 0),
(131, 'Tưng bừng ưu đãi khi mua căn hộ Anland Complex', 'Tập đoàn Nam Cường dành nhiều ưu đãi đặc biệt cùng các phần quà hấp dẫn cho khách hàng đặt mua căn hộ cao cấp Anland Complex nhân sự kiện bán hàng đặc biệt sẽ diễn ra vào ngày 25/6/2017.', '20170621_594a0588d0353.jpg', '2017-06-21', 'https://kenhbds.vn/thong-tin/tung-bung-uu-dai-khi-mua-can-ho-anland-complex.html', '  		&nbsp;  	', 0),
(132, 'Bất động sản Hạ Long bao giờ mới có chỗ cho tiện ích sống?', 'So với cả nước, trong 2 thập kỷ gần đây, Quảng Ninh có mức tăng trưởng kinh tế vượt bậc. Theo số liệu thống kê mới nhất, năm 2016 GPD tăng trưởng của địa phương đạt 10,1% trong khi mức tăng trưởng của cả nước là 6,5%. Chất lượng cuộc sống ngày càng cao so', '20170621_594a15048b693.jpg', '2017-06-21', 'https://kenhbds.vn/thong-tin/bat-dong-san-ha-long-bao-gio-moi-co-cho-cho-tien-ich-song.html', '  	Tại các thành phố lớn ở Việt Nam như Hà Nội, TP HCM, Đà Nẵng, Nha Trang, Quảng Ninh việc sinh sống tại các khu căn hộ đẳng cấp cũng trở thành ưu tiên lựa chọn số 1 của cư dân bởi sinh sống ở các khu căn hộ có nhiều ưu điểm nổi trội về thiết kế, quy hoạch… và đặc biệt là mức độ an ninh, an toàn và hệ thống tiện ích đồng bộ cho cuộc sống.  ', 0),
(133, 'Ciputra ra mắt dự án biệt thự \"sang chảnh\" Gardenville Tây Hồ Residence', 'Tập đoàn Ciputra vừa chính thức giới thiệu Gardenville Tây Hồ Residence – khu biệt thự cao cấp mới được phát triển tại Khu đô thị Ciputra Hà Nội.', '20170621_5949c242dc3ce.jpg', '2017-06-21', 'https://kenhbds.vn/thong-tin/ciputra-ra-mat-du-an-biet-thu-sang-chanh-gardenville-tay-ho-residence.html', '  	Khu biệt thự mang tên Gardenville Tây Hồ Residence đặt tại vị trí trung tâm thơ mộng trong lòng Ciputra Hà Nội. Từ đây, cư dân của dự án có thể kết nối nhanh chóng với các quận trung tâm của Hà Nội qua các cung đường rộng, thoáng như đường Võ Chí Công, Âu Cơ...&nbsp; Dự án cũng chỉ cách sân bay Nội Bài chỉ khoảng 15 phút chạy xe.  ', 0),
(134, 'Nhà, đất đóng băng có cơ hội sống lại', 'Nghị quyết xử lý nợ xấu sẽ làm lưu động hóa các tài sản lâu nay nằm bất động.', '20170622_594b3fc014c5c.jpg', '2017-06-22', 'https://kenhbds.vn/thong-tin/nha-dat-dong-bang-co-co-hoi-song-lai.html', '  	Theo báo cáo của Thống đốc Ngân hàng Nhà nước (NHNN) Lê Minh Hưng trước Quốc hội, đến cuối năm ngoái, nếu tính tổng cả nợ xấu nội bảng, nợ có nguy cơ tiềm ẩn và nợ bán cho Công ty Quản lý tài sản của các tổ chức tín dụng Việt Nam (VAMC) chưa được xử lý thì tổng nợ xấu trong toàn hệ thống là 10,08%, tương đương khoảng 600.000 tỉ đồng.  ', 0),
(135, 'TPHCM: Danh sách mới nhất 7 dự án chung cư đủ điều kiện bán nhà hình thành trong tương lai', 'Sở Xây dựng TP.HCM vừa công bố danh sách 7 dự án đủ điều kiện được bán nhà ở hình thành trong tương lai.', '20170809_598a87515abec-130x100.jpg', '2017-06-22', 'https://kenhbds.vn/thong-tin/tphcm-danh-sach-moi-nhat-7-du-an-chung-cu-du-dieu-kien-ban-nha-hinh-thanh-trong-tuong-lai.html', '  	Địa ốc Him Lam cam kết chưa thế chấp quyền sử dụng đất, quyền sở hữu nhà ở và tài sản khác gắn liền trên đất và nhà ở hình thành trong tương lai của dư án cho bất kỳ tổ chức, cá nhân nào và cam kết chịu trách nhiệm trước pháp luật về nội dung cam kết này.  ', 0),
(136, 'Có tiền, nên đầu tư vào đất nền thời điểm này hay không?', 'Cơn sốt đất đang kích thích tâm lý của cả nhà đầu tư cá nhân lẫn người có tiền nhàn rỗi vào cuộc. Tuy nhiên tỷ lệ nắm bắt cơ hội là rất nhỏ và chỉ dành cho những người kinh nghiệm.', '20170503_590954c897978.jpg', '2017-05-04', 'https://kenhbds.vn/thong-tin/co-tien-nen-dau-tu-vao-dat-nen-thoi-diem-nay-hay-khong.html', '  	Giá trị của phân khúc đất nền ở một số khu vực tại TP.HCM đang tăng lên từng ngày, vượt đỉnh lịch sử năm 2007. Nhưng giao dịch thời điểm này tương đối cạm bẫy đối với người mới tham gia, vì lực lượng cò đất đã “phủ sóng” khắp nơi.  ', 0),
(137, 'Bộ Xây dựng cho phép xây dựng căn hộ 25m2', 'Bộ Xây dựng vừa ký văn bản đồng ý cho công ty TNHH Địa ốc Đất Lành có thể xây dựng căn hộ diện tích 25m2.', '20170504_590a94fc7cf5b.jpg', '2017-05-04', 'https://kenhbds.vn/thong-tin/bo-xay-dung-cho-phep-xay-dung-can-ho-25m2.html', '  	Bộ cũng yêu cầu căn hộ thương mại dưới 25 m2 phải đảm bảo thiết kế, xây dựng theo kiểu căn hộ khép kín.  ', 0),
(138, 'Lý do đã khiến CR7 quyết định mua nhà tại Cocobay?', 'CR7 cho rằng Cocobay Towers là một minh chứng tiêu biểu cho tính cách của anh – tràn đầy nhiệt huyết, mạnh mẽ vươn lên, nỗ lực không ngừng nghỉ.', '20170504_590a9610367f6.jpg', '2017-05-04', 'https://kenhbds.vn/thong-tin/ly-do-da-khien-cr7-quyet-dinh-mua-nha-tai-cocobay.html', '  	CR7 cho rằng Cocobay Towers là một minh chứng tiêu biểu cho tính cách của anh – tràn đầy nhiệt huyết, mạnh mẽ vươn lên, nỗ lực không ngừng nghỉ. Bản năng anh ấy là khí chất tiên phong và nội lực của người chiến thắng. CR7 cũng rất thích thú khi biết đến tinh thần \"sống hết mình\" của Cocobay và bày tỏ ủng hộ những giá trị cộng đồng mà tôi cùng các cộng sự Empire Group hướng đến để mang lại cho địa phương, đất nước mình.  ', 0),
(139, 'Thị trường BĐS nghỉ dưỡng: Nha Trang dẫn đầu cả nước quý 1 năm 2017', 'Với lượng cung lớn của bất động sản nghỉ dưỡng, đặc biệt là căn hộ nghỉ dưỡng sẽ khiến sự cạnh tranh trên thị trường sẽ ngày càng gay gắt hơn, chính sự cạnh tranh này sẽ tạo nên những sản phẩm bất động sản nghỉ dưỡng khác biệt trong năm nay.', 'thi-truong-du-lich-nghi-duong-Nha-Trang-130x100.jpg', '2017-05-04', 'https://kenhbds.vn/thong-tin/thi-truong-bds-nghi-duong-nha-trang-dan-dau-ca-nuoc-quy-1-nam-2017.html', '  	Cũng theo ông Đính, sự phát triển mạnh mẽ của thị trường bất động sản Nha Trang trong quý 1 vừa qua chủ yếu đến từ lượng khách du lịch tại Nha Trang thời gian gần đây tăng trưởng cao. Điều này đã tác động đến công suất thuê phòng tại Nha Trang. Chính điều này đã dẫn đến phân khúc căn hộ khách sạn tại Nha Trang trở nên hấp dẫn nhà đầu tư hơn bao giờ hết.  ', 0);
INSERT INTO `projects` (`id`, `title`, `description`, `sellers`, `id_image`, `address`, `overview`, `utility`, `location`, `vip`, `view`, `link`) VALUES
(115, 'Khu đô thị Aurora Đà Nẵng City', '                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Là khu đô thị vừa thích hợp để xây nhà ở, đầu tư, kinh doanh</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Hạ tầng hoàn chỉnh, pháp lý rõ ràng</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Quy hoạch chuẩn mực, đồng bộ : thiết kế theo phong cách phố thương mại hiện đại – khoa học.</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Giá bán hấp dẫn, dịch vụ hoàn chỉnh sau khi bán và giá trị gia tăng cao trong thời gian ngắn.</li>                                              ', '<a href=\"#uu-nhuoc-diem\">Ưu nhược điểm của khu đô thị Aurora Đà Nẵng City?</a>', 1, 'Phường Hòa Minh , Quận Liên Chiểu, TP Đà Nẵng', '<h4><i class=\"fa fa-arrow-circle-right\"></i>Giới thiệu khu đô thị Aurora Da Nang City                            </h4><br /><br /><br />Mục Lục<br /> <li><a href=\"#gioi-thieu\">Tổng quan dự án chung cư Khu đô thị Aurora Đà Nẵng City</a></li> <li><a href=\"#can-biet\">Mua chung cư Khu đô thị Aurora Đà Nẵng City cần biết</a></li> <ul> <li><a href=\"#tien-ich-du-an\">Khu đô thị Aurora Đà Nẵng City có những tiện ích gì?</a></li> <li><a href=\"#vi-tri-du-an\">Khu đô thị Aurora Đà Nẵng City nằm ở đâu?</a></li> <li><a href=\"#uu-nhuoc-diem\">Ưu nhược điểm của khu đô thị Aurora Đà Nẵng City?</a></li> <br />Mua khu đô thị Aurora Đà Nẵng City nên biết <br /><br />', '<h4><i class=\"fa fa-user-md\"></i>Tiện ích khu đô thị Aurora Da Nang City                             </h4><br /><br />Aurora Da Nang City được quy hoạch theo mô hình khu đô thị sinh thái, thiết kế hiện đại, văn minh, đảm bảo hạ tầng kỹ thuật đồng bộ và các công trình hạ tầng xã hội như trung tâm thương mại, công viên cây xanh, sân bóng rổ, cầu lông, khu thể thao ngoài trời, sân bóng đá mini, phố đi dạo…  Lân cận dự án có đầy đủ các dịch vụ gia tăng phục vụ các nhu cầu cuộc sống thường ngày của cư dân.<br /><br />Trung tâm thương mại được bố trí ngay tuyến đường chính đi vào dự án<br /> 2 công viên phía Tây cùng hệ thống trò chơi cho trẻ em hiện đại tạo nên hệ sinh thái, giáo dục hiện đại và hiệu quả<br /><br />Khu công viên và sân tập thể dục thể thao, sân cầu lông, tennis, bóng rổ được đầu tư xây dựng để đảm bảo sức khỏe cho cư dân<br /><br />', '<h4><i class=\"fa fa-map-marker\"></i>Vị trí khu đô thị Aurora Da Nang City                             </h4><br /><span id=\"vi-tri-du-an\"></span><br />Aurora Da Nang City tọa lạc tại phường Hòa Minh, Q. Liên Chiểu thuộc khu vực Tây Bắc là phân vùng được thành phố Đà Nẵng định hướng phát triển du lịch, nghĩ dưỡng, trung tâm thương mại &#8211; dịch vụ , giao thông vận tải và kinh tế biển. Thêm vào đó, Đà Nẵng cũng ưu tiên phát triển ngành công nghiệp công nghệ cao, công nghệ thông tin, làng đại học quốc tế tại khu vực Tây Bắc.<br /><br />', 1, 0, 'https://duan.muabannhadat.vn/khu-thi-aurora-da-nang-city.html'),
(116, 'KĐT Coco Center House', '                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Vị trí đắc địa</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Thích hợp để xây nhà ở, đầu tư, kinh doanh</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Hạ tầng hoàn chỉnh, pháp lý rõ ràng.</li>                                                     <li><i class=\"fa fa-caret-right\" aria-hidden=\"true\"></i> Quy hoạch chuẩn mực, đồng bộ</li>                                              ', '<a href=\"#vi-tri-du-an\">Khu đô thị Coco Center House nằm ở đâu?    </a>', 2, 'Xã Điện Ngọc, Điện Bàn, Quảng Nam', '<h4><i class=\"fa fa-arrow-circle-right\"></i>Giới thiệu khu đô thị Coco Center House                            </h4><br /> Khu đô thị Coco Center House sở hữu vị trí đắc địa khi nằm cạnh thiên đường du lịch và giải trí CocoBay Đà Nẵng, cùng hệ thống tiện ích đẳng cấp, không gian sống trong lành đã và đang tạo ấn tượng tốt trên thị trường bất động sản Đà Nẵng trong thời gian gần đây. Dự án do Công Ty TNHH Sản Xuất và Thương Mại Bách Đạt đầu tư. <br /> Hội tụ được rất nhiều lợi thế, những giá trị vượt trội bên trong và lân cận dự án, Dự án Coco Center House đang là một trong những dự án đô thị hàng đầu trục ven biển miền Trung hứa hẹn mang đến cho các nhà đầu tư một kênh đầu tư hấp dẫn với tỷ suất sinh lời cao. <br />Mục Lục<br /> <li><a href=\"#gioi-thieu\">Tổng quan dự án khu đô thị Coco Center House    </a></li> <li><a href=\"#can-biet\">Những điều người mua khu đô thị Coco Center House cần biết     </a></li> <ul> <li><a href=\"#thiet-ke-du-an\">Thiết kế khu đô thị Coco Center House có gì nổi bật?    </a></li> <li><a href=\"#tien-ich-du-an\">Khu đô thị Coco Center House có những tiện ích gì?    </a></li> <li><a href=\"#vi-tri-du-an\">Khu đô thị Coco Center House nằm ở đâu?    </a></li> <li><a href=\"#uu-nhuoc-diem\">Ưu nhược điểm của khu đô thị Coco Center House?     </a></li> </ul> <br /> Những điều nên biết về KĐT Coco Center House:<br /> <br /> <li> Tên dự án: khu đô thị Coco Center House </li> <li> Vị trí dự án: Xã Điện Ngọc, Điện Bàn, Quảng Nam </li> <li> Chủ đầu tư: Công Ty TNHH Sản Xuất và Thương Mại Bách Đạt  </li> <li> Quy mô dự án: 297.817 m2. </li> <li> Đất công trình công cộng: 16.091 m2 (gồm trung tâm thương mại, bệnh viện tư)  </li> <li> Đất khai thác hỗn hợp: 1.680 m2 (nhà trẻ, trường học mẫu giáo và các cấp 1-2-3) </li> <li> Đất công viên cây xanh, khu vơi chơi thể dục thể thao: 5.236 m2  </li> <li> Đất công trình giao thông, hạ tầng kỹ thuật: 90.834 m2  </li> <li> Đất nhà phố liền kề: 132.960 m2 </li> <li> Đất biệt thự: 4.842 m2  </li> <li> Đất ở chỉnh trang và tái định cư: 46.174 m2. </li> <br />', '<h4><i class=\"fa fa-user-md\"></i>Khu đô thị Coco Center House có tiện ích gì?                            </h4><br /> Với việc dành gần 50% quỹ đất để phát triển các tiện nghi- tiện ích nội khu, chủ đầu tư khu đô thị Coco Center House đảm bảo mang đến cho cư dân một cuộc sống nghỉ dưỡng thư thái, một kỳ nghỉ thú vị. <br /> Coco Center House hội tụ đầy đủ các tiện ích nội khu như trung tâm thương mại, công viên, bệnh viện và chỉ cách biển 500m… sẵn sàng đáp ứng nhu cầu đa dạng của cư dân tương lai. <br /><br /> Bao quanh dự án Coco Center House là các biệt thự tráng lệ, các sân golf đẳng cấp quốc tế, các resort lớn ven biển Đà Nẵng – Hội An, khu vui chơi, khu công nghiệp, khu điều dưỡng … Dự án Coco Center House còn cách các tiện ích sống quan trọng như bệnh viện, siêu thị, trung tâm hành chính quận, ngân hàng… trong bán kính từ 3 – 4km. <br /> Khu đô thị Coco Center House có ưu điểm gì?<br /> <br /> Các tiện ích nội khu lẫn ngoại khu được hội tụ đầy đủ đáp ứng hoàn hảo giá trị 3 trong 1: nhà ở, thương mại và nghỉ dưỡng cao cấp mang đến luồng “sinh khí” mới cho cư dân nơi đây. <br /><br />', '<h4><i class=\"fa fa-map-marker\"></i>Khu đô thị Coco Center House ở đâu?                            </h4><br /> KĐT ven biển Coco Center House tọa lạc tại vị trí đắc địa, nằm ngay trên tuyến đường 5 sao ven biển Đà Nẵng – Hội An, sát sông Cổ Cò đang sở hữu tiềm năng lớn về du lịch, nghỉ dưỡng, dự án Coco Center House được xem như một nét chấm phá độc đáo hiện hữu trên bức tranh sinh động cuộc sống của khu Nam Đà Nẵng. <br /> Khu đô thị Coco Center House được quy hoạch với sản phẩm đa dạng. Đất nền nhà phố có diện tích 120 m2 (5&#215;24), 125m2 (5&#215;25); đất nền nhà phố chuyên kinh doanhcó diện tích 137m2 (5&#215;27); đất nền biệt thự có diện tích từ 187 m2 – 200 m2. <br /> <li> Phía Bắc giáp KĐT FPT City. </li> <li> Phía Nam giáp KĐT Phú Thạnh Mỹ. </li> <li> Phía Đông giáp sông Cổ Cò.</li> <li> Phía Tây giáp công viên trung tâm 11ha </li> <br />', 1, 0, 'https://duan.muabannhadat.vn/khu-do-thi-coco-center-house-quang-nam.html'),
